mapper.select:
SELECT COUNT(*)
FROM product
WHERE PRODUCT_SALE_STATUS = '판매중';

SELECT COUNT(*)
FROM product
WHERE PRODUCT_SALE_STATUS = '판매중지';

SELECT COUNT(*)
FROM product
WHERE PRODUCT_SALE_STATUS = '품절';

mapper.update:
UPDATE product
SET PRODUCT_SALE_STATUS = #{status}
WHERE PRODUCT_ID = #{productId};
