mapper.select:
SELECT CART_ID
FROM cart
WHERE USER_ID_NO = #{userIdNo};

SELECT COUNT(*)
FROM cart_product cp
JOIN cart c ON c.CART_ID = cp.CART_ID AND c.USER_ID_NO = #{userIdNo}

mapper.update:
UPDATE cart_product
SET PRODUCT_COUNT = #{productCount}
WHERE CART_ID = #{cartId} AND PRODUCT_ID = #{productId};
